import axios from "axios";

const baseURL = "http://192.168.1.11:5000"; // URL backend kamu

const API = axios.create({
  baseURL: baseURL,
});

export default {

  // Register User
  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  // Login User
  loginUser(userData) {
    return API.post("/users/login", userData);
  },

  // Get User By ID
  getUserById(id) {
    return API.get(`/users/${id}`);
  },

  // Get All Users
  getAllUsers(page = 1, limit = 10) {
    return API.get(`/users?page=${page}&limit=${limit}`);
  },

  // Update User Profile
  updateUserProfile(userData) {
    return API.put("/users/update", userData);
  },

  // Get All Pengaduans
  getAllPengaduans(page = 1, limit = 10) {
    return API.get(`/pengaduan?page=${page}&limit=${limit}`);
  },

  // Get Pengaduan By ID
  getPengaduanById(id) {
    return API.get(`/pengaduan/${id}`);
  },

  // Create Pengaduan
  createPengaduan(formData) {
    return API.post("/pengaduan", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    })
    .then(response => {
      console.log("Complaint Created:", response.data);
      return response.data;
    })
    .catch(error => {
      console.error("Error creating complaint:", error.response ? error.response.data : error.message);
      throw error;
    });
  },

  // Update Tindak Lanjut Pengaduan
  updateTindakLanjut(id, tindakLanjutStatus) {
    return API.put(`/pengaduan/${id}/tindak-lanjut`, { tindakLanjut: tindakLanjutStatus });
  },

  // Delete Pengaduan
  deletePengaduan(id) {
    return API.delete(`/pengaduan/${id}`);
  },

  // Get Pendaftaran By ID
  getPendaftaranById(id) {
    return API.get(`/pendaftaran/${id}`);
  },

  // Create Pendaftaran
  createPendaftaran(userData) {
    return API.post("/pendaftaran", userData)
      .then(response => {
        console.log("Pendaftaran Created:", response.data);
        return response.data;
      })
      .catch(error => {
        console.error("Error creating pendaftaran:", error.response ? error.response.data : error.message);
        throw error;
      });
  },

  // Update Pendaftaran
  updatePendaftaran(id, userData) {
    return API.put(`/pendaftaran/${id}`, userData)
      .then(response => {
        console.log("Pendaftaran Updated:", response.data);
        return response.data;
      })
      .catch(error => {
        console.error("Error updating pendaftaran:", error.response ? error.response.data : error.message);
        throw error;
      });
  },

  // Delete Pendaftaran
  deletePendaftaran(id) {
    return API.delete(`/pendaftaran/${id}`)
      .then(response => {
        console.log("Pendaftaran Deleted:", response.data);
        return response.data;
      })
      .catch(error => {
        console.error("Error deleting pendaftaran:", error.response ? error.response.data : error.message);
        throw error;
      });
  },

  // Upload Foto untuk Pendaftaran
  uploadFoto(id, formData) {
    return API.post(`/pendaftaran/${id}`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    })
    .then(response => {
      console.log("Foto Uploaded:", response.data);
      return response.data;
    })
    .catch(error => {
      console.error("Error uploading foto:", error.response ? error.response.data : error.message);
      throw error;
    });
  },

  // Get All Pendaftaran
  getAllPendaftaran(page = 1, limit = 10) {
    return API.get(`/pendaftaran?page=${page}&limit=${limit}`);
  }
};
