import { createRouter, createWebHistory } from 'vue-router'
import LoginView from '@/views/LoginView.vue'
import ForgotPasswordView from '@/views/ForgotPasswordView.vue'
import OtpView from '@/views/OtpView.vue'
import SignUpView from '@/views/SignUpView.vue'
import ResetPassView from '@/views/ResetPassView.vue'
import PortalBeritaView from '@/views/PortalBeritaView.vue'
import TambahBeritaView from '@/views/admin/TambahBeritaView.vue'
import BeritaView from '@/views/admin/BeritaView.vue'
import SponsorView from '@/views/admin/SponsorView.vue'
import CsView from '@/views/admin/CsView.vue'
import TransaksiView from '@/views/admin/TransaksiView.vue'
import UtangView from '@/views/admin/UtangView.vue'
import ChatMulaiVIew from '@/views/ChatMulaiVIew.vue'
import DataUangView from '@/views/admin/DataUangView.vue'
import ProfileView from '@/views/user/ProfileView.vue'
import DetailBerita from '@/views/DetailBerita.vue'; 
import PengajuanSertifikat from '@/views/user/PengajuanSertifikat.vue'
import RiwayatPengajuan from '@/views/user/RiwayatPengajuan.vue'
import RegisterPusatView from '@/views/pusat/RegisterPusatView.vue'
import DataGrafikView from '@/views/pusat/DataGrafikView.vue'
import PusatCsView from '@/views/pusat/PusatCsView.vue'
import PortalBeritaPusatView from '@/views/pusat/PortalBeritaPusatView.vue'
import KelolaBeritaView from '@/views/pusat/KelolaBeritaView.vue'
import AdminPengajuan from '@/views/admin/AdminPengajuan.vue'
import PengajuanSertifikatPusat from '@/views/pusat/PengajuanSertifikatPusat.vue'
import ProfilePusatView from '@/views/pusat/ProfilePusatView.vue'
import PortalBeritaUserView from '@/views/user/PortalBeritaUserView.vue'
import AboutView from '@/views/AboutView.vue'
import FormPengaduanView from '@/views/user/FormPengaduanView.vue'


const routes = [
  {
    path: '/login',
    name: 'Login',
    component: LoginView
  },
  {
    path: '/lupapassword',
    name: 'ForgotPasswordView',
    component: ForgotPasswordView
  },
  {
    path: '/getotp',
    name: 'OTPView',
    component: OtpView
  },
  {
    path: '/register',
    name: 'SignUpView',
    component: SignUpView
  },
  {
    path: '/resetpassword',
    name: 'ResetPassView',
    component: ResetPassView
  },
  {
    path: '/',
    name: 'Portal berita',
    component: PortalBeritaView
  },
  {
    path: '/tambahberita',
    name: 'TambahBeritaView',
    component: TambahBeritaView
  },
  {
    path: '/admin/berita',
    name: 'Berita',
    component: BeritaView
  },
  {
    path: '/admin/sponsor',
    name: 'SponsorView',
    component: SponsorView
  },
  {
    path: '/admin/customerservices',
    name: 'CsView',
    component: CsView
  },
  {
    path: '/admin/transaksi',
    name: 'Transaksi',
    component: TransaksiView
  },
  {
    path: '/admin/utang',
    name: 'UtangView',
    component: UtangView
  },
  {
    path: '/chat',
    name: 'UserChat',
    component: ChatMulaiVIew
  },
  {
    path: '/profile',
    name: 'ProfileView',
    component: ProfileView
  },
  {
    path: '/admin/data-uang',
    name: 'Datauang',
    component: DataUangView
  },
  {
    path: '/news/:id',  // Dynamic route to capture the news ID
    name: 'DetailBerita',
    component: DetailBerita,
  },
  {
    path: '/pengajuan-sertifikat',
    name: 'PengajuanSertifikat',
    component: PengajuanSertifikat
  },
  {
    path: '/riwayat-pengajuan',
    name: 'RiwayatPengajuan',
    component: RiwayatPengajuan
  },
  {
    path: '/admin/pengajuan',
    name: 'AdminPengajuan',
    component: AdminPengajuan
  },
  {
    path: '/pusat/dataregistrasi',
    name: 'RegisterPusatView',
    component: RegisterPusatView
  },
  {
    path: '/pusat/datakeuangan',
    name: 'DataKeuangan',
    component: DataGrafikView
  },
  {
    path: '/pusat/customerservices',
    name: 'PusatCustomerServices',
    component: PusatCsView
  },
  {
    path: '/pusat/portal-berita',
    name: 'PusatPortalBerita',
    component: PortalBeritaPusatView
  },
  {
    path: '/pusat/kelola-berita',
    name: 'PusatKelolaBerita',
    component: KelolaBeritaView
  },
  {
    path: '/pusat/pengajuan-sertifikat',
    name: 'PengajuanSertifikatPusat',
    component: PengajuanSertifikatPusat
  },
  {
    path: '/pusat/profile',
    name: 'Profile Pusat',
    component: ProfilePusatView
  },
  {
    path: '/user/portal-berita',
    name: 'userportalberita',
    component: PortalBeritaUserView
  },
  {
    path: "/tentang-kami",
    name: "AboutView",
    component: AboutView
  }
]


const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
