import axios from "axios";

const baseURL = "http://192.168.1.11:5000";

const API = axios.create({
  baseURL: baseURL,
});

export default {
  // ** Berita CRUD **
  getNews(page = 1, limit = 10) {
    return API.get(`/news?page=${page}&limit=${limit}`);
  },

  createNews(formData) {
    return API.post("/news", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  updateNews(id, formData) {
    return API.put(`/news/${id}`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  deleteNews(id) {
    return API.delete(`/news/${id}`);
  },

  // ** User Management **
  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  loginUser(userData) {
    return API.post("/users/login", userData);
  },

  // **✅ Memperbarui Data Profil User**
  getIser(userData) {
    return API.put("/users/${id}", userData);
  },

  // **✅ Mengubah Password User**
  changePassword(passwordData) {
    return API.put("/users/change-password", passwordData);
  },

  GetpathImage(img) {
    return `${baseURL}${img}`;
  },

  // ** Pengaduan CRUD **

  // Mendapatkan semua pengaduan
  getAllPengaduans(page = 1, limit = 10) {
    return API.get(`/pengaduan?page=${page}&limit=${limit}`);
  },

  // Mendapatkan pengaduan berdasarkan ID
  getPengaduanById(id) {
    return API.get(`/pengaduan/${id}`);
  },

  // Membuat pengaduan baru
  createPengaduan(formData) {
    return API.post("/pengaduan", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    })
    .then(response => {
      console.log("Complaint Created:", response.data);
      return response.data;
    })
    .catch(error => {
      console.error("Error creating complaint:", error.response ? error.response.data : error.message);
      throw error;
    });
  },

  // Mengupdate tindak lanjut pengaduan
  updateTindakLanjut(id, tindakLanjutStatus) {
    return API.put(`/pengaduan/${id}/tindak-lanjut`, { tindakLanjut: tindakLanjutStatus });
  },

  // Menghapus pengaduan berdasarkan ID
  deletePengaduan(id) {
    return API.delete(`/pengaduan/${id}`);
  }
};
