import axios from "axios";

const baseURL = "http://192.168.1.35:5000"; 

const API = axios.create({
  baseURL: baseURL
});

export default {
  getBerita() {
    return API.get("/berita");
  },

  getBeritaById(id) {
    return API.get(`/berita/${id}`);
  },

  CreateBerita(formData) {
    return API.post("/berita", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  updateBerita(id, beritaData) {
    return API.put(`/berita/${id}`, beritaData);
  },

  deleteBerita(id) {
    return API.delete(`/berita/${id}`);
  },

  uploadGambar(beritaId, formData) {
    return API.post(`/berita/${beritaId}/gambar`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  deleteGambar(beritaId, gambarId) {
    return API.delete(`/berita/${beritaId}/gambar/${gambarId}`);
  },

  // Register a new user
  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  // Login an existing user
  loginUser(userData) {
    return API.post("/users/login", userData);
  }
};
