import axios from "axios";

const baseURL = "http://localhost:5000"; 

const API = axios.create({
  baseURL: baseURL,
});

export default {
  // Fungsi untuk mengambil semua berita
  getBerita() {
    return API.get("/berita");
  },

  // Fungsi untuk mengambil berita berdasarkan ID
  getBeritaById(id) {
    return API.get(`/berita/${id}`);
  },

  // Fungsi untuk membuat berita baru
  createBerita(formData) {
    return API.post("/berita", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  // Fungsi untuk memperbarui berita
  updateBerita(id, beritaData) {
    return API.put(`/berita/${id}`, beritaData);
  },

  // Fungsi untuk menghapus berita
  deleteBerita(id) {
    return API.delete(`/berita/${id}`);
  },

  // Fungsi untuk menambah gambar berita
  uploadGambar(beritaId, formData) {
    return API.post(`/berita/${beritaId}/gambar`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  // Fungsi untuk menghapus gambar berita
  deleteGambar(beritaId, gambarId) {
    return API.delete(`/berita/${beritaId}/gambar/${gambarId}`);
  },

  // Register user
  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  // Login user
  loginUser(userData) {
    return API.post("/users/login", userData);
  }
};
