import axios from "axios";

const baseURL = "http://192.168.1.11:5000";

const API = axios.create({
  baseURL: baseURL,
});

export default {
  getNews() {
    return API.get(`/news?page=${page}&limit=${limit}`);
  },
  getNewsById(id) {
    return API.get(`/news/${id}`);
  },

  createNews(formData) {
    return API.post("/news", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  updateBerita(id, formData) {
    return API.put(`/news/${id}`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  deleteNews(id) {
    return API.delete(`/news/${id}`);
  },

  deleteGambar(gambarId) {
    return API.delete(`/${gambarId}/gambar`);
  },

  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  loginUser(userData) {
    return API.post("/users/login", userData);
  },
};
