// src/store/index.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    userStatus: 'Disetujui', // Atau 'Diproses', 'Ditolak' sesuai dengan logika Anda
  },
  mutations: {
    // Mutation untuk memperbarui status pengguna jika diperlukan
    setUserStatus(state, status) {
      state.userStatus = status;
    }
  },
  actions: {
    // Action untuk mengatur status melalui mutation
    updateUserStatus({ commit }, status) {
      commit('setUserStatus', status);
    }
  }
});
