import axios from "axios";

const baseURL = "http://192.168.1.35:5000"; // Sesuaikan dengan IP backend Anda

const API = axios.create({
  baseURL: baseURL
});

export default {
  getBerita() {
    return API.get("/berita"); // GET /berita
  },

  getBeritaById(id) {
    return API.get(`/berita/${id}`); // GET /berita/:id
  },

  CreateBerita(formData) {
    return API.post("/berita", formData, { // POST /berita
      headers: {
        "Content-Type": "multipart/form-data", // Kirim gambar dengan FormData
      },
    });
  },

  updateBerita(id, formData) {
    return API.put(`/berita/${id}`, formData); // PUT /berita/:id
  },

  deleteBerita(id) {
    return API.delete(`/berita/${id}`); // DELETE /berita/:id
  },

  uploadGambar(beritaId, formData) {
    return API.post(`/berita/${beritaId}/gambar`, formData, { // POST /berita/:id/gambar
      headers: {
        "Content-Type": "multipart/form-data", // Kirim gambar dengan FormData
      },
    });
  },

  deleteGambar(beritaId, gambarId) {
    return API.delete(`/berita/${beritaId}/gambar/${gambarId}`); // DELETE /berita/:id/gambar/:gambarId
  },

  // Register a new user
  registerUser(userData) {
    return API.post("/users/register", userData); // POST /users/register
  },

  // Login an existing user
  loginUser(userData) {
    return API.post("/users/login", userData); // POST /users/login
  }
};
