import axios from "axios";

const baseURL = "http://192.168.1.11:5000";

const API = axios.create({
  baseURL: baseURL,
});

// Tambahkan token dari localStorage ke setiap request
API.interceptors.request.use((config) => {
  const token = localStorage.getItem("authToken");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export default {
  getNews(page = 1, limit = 10) {
    return API.get(`/news?page=${page}&limit=${limit}`);
  },

  createNews(formData) {
    return API.post("/news", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  updateNews(id, formData) {
    return API.put(`/news/${id}`, formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
  },

  deleteNews(id) {
    return API.delete(`/news/${id}`);
  },

  registerUser(userData) {
    return API.post("/users/register", userData);
  },

  loginUser(userData) {
    return API.post("/users/login", userData);
  },

  // **✅ Mendapatkan Data User yang Sedang Login**
  getUserProfile() {
    return API.get("/users/profile");
  },

  // **✅ Memperbarui Data Profil User**
  updateUserProfile(userData) {
    return API.put("/users/profile", userData);
  },

  // **✅ Mengubah Password User**
  changePassword(passwordData) {
    return API.put("/users/change-password", passwordData);
  },

  GetpathImage(img) {
    return `${baseURL}${img}`;
  },
};
